import React, { useEffect, useState } from 'react'
import { Card, Col, Row, List, Avatar, Drawer } from 'antd';
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';
import * as Echarts from 'echarts'

const { Meta } = Card;
export default function Home() {

    const [visible, setvisible] = useState(false);

    useEffect(() => {
        var myChart = Echarts.init(document.getElementById('main'));

        // 指定图表的配置项和数据
        var option = {
            title: {
                text: '新闻浏览量图示'
            },
            tooltip: {},
            legend: {
                data: ['数量']
            },
            xAxis: {
                data: ['生活理财', '科学技术', '军事世界', '世界体育', '环球经济', '时事新闻']
            },
            yAxis: {},
            series: [
                {
                    name: '数量',
                    type: 'bar',
                    data: [5, 20, 36, 10, 12, 20]
                }
            ]
        };

        // 使用刚指定的配置项和数据显示图表。
        myChart.setOption(option);
    }, [])

    return (
        <div className="site-card-wrapper">
            <Row gutter={16}>
                <Col span={8}>
                    <Card title="用户最常浏览" bordered={true}>
                        <List
                            size="small"
                            // bordered
                            dataSource={["军事新闻", "科学新闻", "美食节目", "综艺节目", "亮剑电视剧", "海贼王动漫"
                                , "爱情三角猫", "教育节目", "学习课堂"]}
                            renderItem={item => <List.Item>{item}</List.Item>}
                        />
                    </Card>
                </Col>
                <Col span={8}>
                    <Card title="用户点赞最多" bordered={true}>
                        <List
                            size="small"
                            // bordered
                            dataSource={["军事新闻", "美食节目", "海贼王动漫", "综艺节目", "科学新闻", "爱情三角猫"
                                , "亮剑电视剧", "学习课堂", "教育节目"]}
                            renderItem={item => <List.Item>{item}</List.Item>}
                        />
                    </Card>
                </Col>
                <Col span={8}>
                    <Card
                        style={{ width: 300 }}
                        cover={
                            <img
                                alt="example"
                                src="https://img1.baidu.com/it/u=356398229,1259493806&fm=253&fmt=auto&app=138&f=JPEG?w=501&h=500"
                            />
                        }
                        actions={[
                            <SettingOutlined key="setting" onClick={()=>{
                                setvisible(true)
                            }}/>,
                            <EditOutlined key="edit" />,
                            <EllipsisOutlined key="ellipsis" />,
                        ]}
                    >
                        <Meta
                            avatar={<Avatar src="https://joeschmoe.io/api/v1/random" />}
                            title="Moira"
                            description="人生就是手脚快的人赢啊，只是傻傻看着，就什么也得不到哦!"
                        />
                    </Card>
                </Col>
            </Row>

            <Drawer title="个人新闻分类" placement="right" 
            onClose={()=>{setvisible(false)}} visible={visible}>
                <p>Some contents...</p>
                <p>Some contents...</p>
                <p>Some contents...</p>
            </Drawer>

            {/* 为 ECharts 准备一个定义了宽高的 DOM  */}
            <div id="main" style={{ width: "80%", height: "400px", marginTop: "30px" }}></div>
        </div>
    )
}
