import React, { useState, useEffect } from 'react'
import { Table, Tag } from 'antd'
import axios from 'axios';

export default function RoleList() {
    const [dataSource, setdataSource] = useState([]);
    const columns = [
        {
            title: 'ID',
            dataIndex: 'id',
            render: (id) => {
                return <b>{id}</b>
            }
        },
        {
            title: '角色名称',
            dataIndex: 'roleName',
        },
        {
            title: '权限路径',
            dataIndex: 'rights',
            render: (rights) => {
                return <Tag color="purple">{rights}</Tag>
            }
        },
        {
            title: '操作',
            render: (item) => {
                return <div>
                    <Button danger type="primary" shape="circle" icon={<DeleteOutlined />}
                        onClick={() => confirmMethod(item)} />
                    <Popover content={<div style={{ textAlign: "center" }}><Switch checked={item.pagepermission}
                        onClick={() => SwitchMethod(item)}>
                    </Switch></div>} title="页面配置项"
                        trigger={item.pagepermission === undefined ? '' : "click"}>
                        <Button type="primary" shape="circle" icon={<EditOutlined />}
                            disabled={item.pagepermission === undefined} />
                    </Popover>
                </div>
            }
        },
    ]
    useEffect(() => {
        axios.get("http://localhost:4000/roles").then(res => {
            // console.log(res.data);
            setdataSource(res.data)
        })
    }, [])
    return (
        <div>
            <Table dataSource={dataSource} columns={columns}
                rowKey={(item) => item.id}></Table>
            {/* 箭头函数返回值是item.id */}
        </div>
    )
}
