import React from 'react'
import { Layout, Menu } from 'antd';
import './index.css'
import {
    UserOutlined,
    VideoCameraOutlined,
    UploadOutlined,
    SettingOutlined,
    HomeOutlined,
    CrownOutlined,
} from '@ant-design/icons'; //导航栏图标

const { Sider } = Layout; //ES6 解构
const { SubMenu } = Menu;

//模拟数组结构
const menuList = [
    {
      key: "/home",
      title: "首页",
      icon: <HomeOutlined />,
    },
    {
      key: "/user-manage",
      title: "用户管理",
      icon: <UserOutlined />,
      children: [
        {
          key: "/user-manage/list",
          title: "用户列表",
          icon: <UserOutlined />,
        },
      ],
    },
    {
      key: "/right-manage",
      title: "权限管理",
      icon: <CrownOutlined />,
      children: [
        {
          key: "/right-manage/role/list",
          title: "角色列表",
          icon: <CrownOutlined />,
        },
        {
          key: "/right-manage/right/list",
          title: "权限列表",
          icon: <CrownOutlined />,
        },
      ],
    },
  ];

export default function SideMenu() {

    const renderMenu = (menuList)=>{
      return menuList.map(item=>{
        if(item.children){
          return <SubMenu key={item.key} icon={item.icon} title="用户管理"></SubMenu>
        }
        return <Menu.Item key={item.key}></Menu.Item>
      })
    }

    return (
        <Sider trigger={null} collapsible collapsed={false} >
            <div className="logo">新闻发布管理系统</div>
            <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                {/* <Menu.Item key="1" icon={<UserOutlined />}>
                    首页
                </Menu.Item>
                <Menu.Item key="2" icon={<VideoCameraOutlined />}>
                    nav 2
                </Menu.Item>
                <Menu.Item key="3" icon={<UploadOutlined />}>
                    nav 3
                </Menu.Item>
                <SubMenu key="sub4" icon={<SettingOutlined />} title="用户管理">
                    <Menu.Item key="9">Option 9</Menu.Item>
                    <Menu.Item key="10">Option 10</Menu.Item>
                    <Menu.Item key="11">Option 11</Menu.Item>
                    <Menu.Item key="12">Option 12</Menu.Item>
                </SubMenu> */}

                {renderMenu(menuList)}

            </Menu>
        </Sider>
    )
}
