import React from "react"
import * as Mui from "@mui/material"
import Swal from "sweetalert2"


function Post({data, getPosts}) {
    let deleteHandler = (id) => {
        fetch("http://localhost:5000/posts/" + id, {
        method: "DELETE",
        headers: {
            "x-auth-token" : localStorage.getItem('token')
        }
        })
        .then(res => res.json())
        .then(data => {
            Swal.fire(data.msg)
            getPosts()
        })
    }
    return(
        <div>
            <h3>{data.title}</h3>
            <p>{data.description}</p>
            <Mui.Button variant="contained" color="secondary">Edit</Mui.Button>
            <Mui.Button variant="contained" color="error" onClick={() => deleteHandler(data._id)}>Delete</Mui.Button>
        </div>
    )
}

export default Post
