import React, { useState, useEffect } from 'react'
import { Button, Table, Tag,Modal } from 'antd'
import axios from 'axios';
import {
    EditOutlined,
    DeleteOutlined,
    ExclamationCircleOutlined,
} from '@ant-design/icons';
const { confirm } = Modal;

export default function RightList() {
    const [dataSource, setdataSource] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:4000/rights?_embed=children").then(res => {
            const list = res.data
            list[0].children = "" //设置权限里面的首页 不可展开
            setdataSource(list) //(useState)把res数据值设置上去
        })
    }, [])

    const columns = [
        {
            title: 'ID',
            dataIndex: 'id',
            render: (id) => {
                return <b>{id}</b>
            }
        },
        {
            title: '权限名称',
            dataIndex: 'title',
        },
        {
            title: '权限路径',
            dataIndex: 'key',
            render: (key) => {
                return <Tag color="purple">{key}</Tag>
            }
        },
        {
            title: '操作',
            render: () => {
                return <div>
                    <Button danger type="primary" shape="circle" icon={<EditOutlined />}
                        onClick={() => confirmMethod()} />
                    <Button type="primary" shape="circle" icon={<DeleteOutlined />} />
                </div>
            }
        },
    ];
    const confirmMethod = () => {
        confirm({
            title: '您确定要删除吗？',
            icon: <ExclamationCircleOutlined />,
            onOk() {
                // console.log('确认');

            },
            onCancel() {
                // console.log('取消');
            },
        });
    }
     const deleteMethod = ()=>{

     }   

    return (
        <div>
            <Table dataSource={dataSource} columns={columns}
                pagination={{
                    pageSize: 5 //（操作）分页设置 每一页只显示5页
                }} />
        </div>
    )
}
